package Collection;
import java.util.*;
public class Apply {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<Person> a = new ArrayList<Person>();
		a.add(new Person("s1",10));
		a.add(new Person("s2",20));
		a.add(new Person("s3",30));
		a.add(new Person("s4",40));
		
		Person p = new Person("s1", 10);
		a.remove(p);
		System.out.println(a);
		System.out.println(a.contains(p));
	}

}

class Person{
	private String name;
	
	private int age;
	
	Person(String name, int age){
		super();
		this.name = name;
	}
	
	public String toString(){
		return  name+age;
	}
}
